import emailjs from "emailjs-com";

const TEMPLATE_ID = process.env.EMAILJS_TEMPLATE_ID;
const USER_ID = process.env.EMAILJS_USER_ID;
const SERVICE_ID = process.env.EMAILJS_SERVICE_ID;

class Form extends React.Component {
  state = {
    name: "",
    email: "",
    message: "",
    response: "",
    loading: false
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const { email, message } = this.state;

    let templateParams = {
      from_name: email,
      to_name: "OJOS",
      message_html: message,
      reply_to: email,
    };

    this.sendEmail(templateParams);
  };

  sendEmail = (templateParams) => {
    this.setState({ loading : true });

    emailjs.send(
      SERVICE_ID, 
      TEMPLATE_ID, 
      templateParams,
      USER_ID
      )
      .then((response) => {
        this.setState({ loading : false });
        this.setState({ response : "Yay! ðŸŽ‰ Your message has been sent. We will get back to you ASAP" });
        this.resetForm();
        console.log('SUCCESS!', response.status, response.text);
      }, (error) => {
        this.setState({ loading : false });
        this.setState({ response : error.text });
        console.log('FAILED...', error);
    });
  }

  resetForm() {
    this.setState({
      name: "",
      email: "",
      message: "",
    });
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <section className="sub-section" id="contact">
        <div>
          <form className="contact-form" onSubmit={this.handleSubmit}>
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleChange}
              placeholder="Name"
              className="contact-form-input"
            />
            <input
              required
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleChange}
              placeholder="Email"
              className="contact-form-input"
            />
            <textarea
              required
              rows="10"
              name="message"
              value={this.state.message}
              onChange={this.handleChange}
              placeholder="Message"
              className="contact-form-input"
            />
            <button type="submit" className="contact-form-input-button" disabled={this.state.loading}>
              {this.state.loading ? "Sending..." : "Send"}
            </button>
            <div>{this.state.response ? this.state.response : " "}</div>
          </form>
        </div>
      </section>
    );
  }
}

export default Form;
